INSERT INTO `filelistother` VALUES (37,'/ckfinder/userfiles/result/201807201447090.jpg','<p><img src=\"/ckfinder/userfiles/result/201807201447090.jpg\" style=\"height:460px; width:690px\" /></p>\r\n',8,'workFlow','2018-07-20 15:11:14','4c4d5a0743d6236b7f597&690.jpg','4c4d5a0743d6236b7f597&690.jpg'),(38,'/ckfinder/userfiles/result/201807201451310.xlsx','<p>/ckfinder/userfiles/result/201807201451310.xlsx</p>\r\n',8,'question','2018-07-21 15:11:59','2018员工T恤数量统计 (1).xlsx','2018员工T恤数量统计 (1).xlsx'),(42,'/ckfinder/userfiles/result/image-20180725221913-1.png','<p><img src=\"/ckfinder/userfiles/result/image-20180725221913-1.png\" style=\"height:600px; width:800px\" /></p>\r\n',8,'workFlow','2018-07-25 14:19:54','货币信贷工作流程图','货币信贷工作流程图'),(43,'/ckfinder/userfiles/result/201807251420448.xls','<p>/ckfinder/userfiles/result/201807251420448.xls</p>\r\n',8,'question','2018-07-25 14:20:47','发现问题清单汇总表——会计财务科上报.xls','发现问题清单汇总表——会计财务科上报.xls'),(48,'/ckfinder/userfiles/result/03.png','<p><p><img alt=\"\" src=\"/ckfinder/userfiles/result/03.png\" style=\"height:517px; width:811px\" /></p>\r\n</p>',10,'workFlow','2018-07-31 08:04:12','中国人民银行巴彦淖尔市中心支行保卫科标准','中国人民银行巴彦淖尔市中心支行保卫科标准'),(53,'/ckfinder/userfiles/result/04.png','<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>一、发行基金调运应急指挥流程图</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong><img alt=\"\" src=\"/ckfinder/userfiles/result/04.png\" style=\"height:270px; width:446px\" /></strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>二、发行库守卫应急指挥流程图</strong></p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"/ckfinder/userfiles/images/05(2).png\" style=\"height:262px; width:467px\" /></p>\r\n\r\n<p style=\"text-align:justify\">发行库守卫应急管理要求</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp; <strong>一、处置原则</strong></p>\r\n\r\n<p style=\"text-align:justify\">运用一切手段，防止犯罪分子抢劫、盗窃发行库</p>\r\n\r\n<p style=\"text-align:justify\"><strong>&nbsp;&nbsp; </strong><strong>二、对应急指挥中心的要求</strong></p>\r\n\r\n<p style=\"text-align:justify\">1<strong>、</strong>及时向当地公安机关报警，要求派出警力到达现场进行处置</p>\r\n\r\n<p style=\"text-align:justify\">2、及时向危机领导小组报告情况。</p>\r\n\r\n<p style=\"text-align:justify\">3、及时通知和召集有关人员。</p>\r\n\r\n<p style=\"text-align:justify\">4<strong>、</strong>指挥保卫人员打击、制服犯罪分子。</p>\r\n\r\n<p style=\"text-align:justify\">5、指挥货币金银工作人员、金融机构工作人员撤离事发现场，阻止行内其他工作人员接近事发现场。</p>\r\n\r\n<p style=\"text-align:justify\">6、遇到犯罪分子劫持人质的，负责与犯罪分子谈判，防止犯罪分子伤害人质。</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp; <strong>&nbsp;</strong><strong>三、对保卫部门的要求</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>&nbsp;&nbsp;&nbsp; </strong>1、门卫发现可疑人员企图进入人民银行院内，要进行阻拦，阻拦未成的，要立即向守库值班人员报告。</p>\r\n\r\n<p style=\"text-align:justify\">2、发现可疑情况，要通过电视监控对可疑人员进行监控录像。</p>\r\n\r\n<p style=\"text-align:justify\">3、接到报告或遇到危机情况要立即进入应急状态。</p>\r\n\r\n<p style=\"text-align:justify\">4、两名守库值班人员，一人立即通过电话或紧急报警装置向当地公安机关&ldquo;110&rdquo;报警，之后向应急指挥中心报告有关情况，另一人准备好值班武器，枪弹结合，做好战斗准备。</p>\r\n\r\n<p style=\"text-align:justify\">5、守库值班人员通过电视监控设备对犯罪分子的活动进行监视并录像，打开警报对犯罪分子进行警告，在情况不明的情况下不得离开守库值班室。</p>\r\n\r\n<p style=\"text-align:justify\">6、按照《专职守护押运人员使用枪支管理条例》规定，守卫人员遭遇有下列紧急情行之一，不使用枪支不足以制止暴力犯罪行为的，可以使用枪支，但必须防止误伤现场其他人员：</p>\r\n\r\n<p style=\"text-align:justify\">（1） 守卫目标受到暴力袭击或者有受到暴力袭击的紧迫危机危险的。</p>\r\n\r\n<p style=\"text-align:justify\">（2） 守卫人员受到暴力袭击危及生命安全或者携带的枪支弹药受到抢夺、</p>\r\n\r\n<p style=\"text-align:justify\">抢劫的。</p>\r\n\r\n<p style=\"text-align:justify\">7、保卫科长集合其他保卫人员携带武器前往事发现场进行处置，保卫人员要坚决服从保卫科长的命令和指挥。</p>\r\n\r\n<p style=\"text-align:justify\">8、将抓获的犯罪分子及时移交公安部门，不得擅自做出处理。</p>\r\n\r\n<p style=\"text-align:justify\">9、保护作案现场，由当地公安部门进行现场勘察，对事发当天的录像资料要立即调取并封存保管。</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>&nbsp;</strong>发行基金调运应急管理要求</p>\r\n\r\n<p style=\"text-align:justify\"><strong>一、处置原则</strong></p>\r\n\r\n<p style=\"text-align:justify\">打击犯罪分子，保护发行基金安全。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>二、对应急指挥中心的要求</strong></p>\r\n\r\n<p style=\"text-align:justify\">1、及时联系事件发生地附近人民银行分支机构给予人员、车辆、食品等救援。</p>\r\n\r\n<p style=\"text-align:justify\">2、决定是否增派车辆和人员执行发行基金调运任务</p>\r\n\r\n<p style=\"text-align:justify\"><strong>三、对发行基金调运人员的要求</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>&nbsp;</strong>&nbsp;1、押运负责人要迅速判明情况，指挥全体押运人员做好战斗准备，打击犯罪分子。</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp; 2、向当地公安部门报警，要求派出警力到达现场处置。</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp; 3、向本行应急指挥中心和事件发生地人民银行分支机构、公安机关、公路铁路部门请求支援。</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp; 4、汽车运输车辆能够正常行驶时，要迅速通过危险地带，不与犯罪分子过多纠缠。</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp; 5、汽车运输车辆因路障或损坏等原因无法正常行驶时，押运负责人要组织押运人员利用车体和周围地形、地物进行掩护，保护自己，打击犯罪分子。</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp; 6、按照《专职守护押运人员使用枪支管理条例》规定，押运人员遭遇有下列紧急情行之一，不使用枪支不足以制止暴力犯罪行为的，可以使用枪支，但必须防止误伤现场其他人员：</p>\r\n\r\n<p style=\"text-align:justify\">（1） 押运物品受到暴力袭击或者有受到暴力袭击的紧迫危机危险的。</p>\r\n\r\n<p style=\"text-align:justify\">（2） 押运人员受到暴力袭击危及生命安全或者携带的枪支弹药受到抢夺、</p>\r\n\r\n<p style=\"text-align:justify\">抢劫的。</p>\r\n\r\n<p style=\"text-align:justify\">7、犯罪分子逃跑时，不可远离车辆进行追击。</p>\r\n\r\n<p style=\"text-align:justify\">8、与当地急救中心或医院联系，及时将受伤人员送往治疗。</p>\r\n\r\n<p style=\"text-align:justify\">9、除留下人员负责处理善后事宜外，其他车辆和人员继续执行发行基金调运任务。</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n',10,'workFlow','2018-07-31 08:11:00','保卫科发行基金调运应急指挥流程图','保卫科发行基金调运应急指挥流程图'),(60,'/ckfinder/userfiles/result/101.png','<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/ckfinder/userfiles/images/102.png\" style=\"height:443px; width:719px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n',25,'workFlow','2018-07-31 09:22:43','备案类账户开立、变更、撤销流程图','备案类账户开立、变更、撤销流程图'),(61,'/ckfinder/userfiles/result/103.png','<p><p><img alt=\"\" src=\"/ckfinder/userfiles/result/103.png\" style=\"height:441px; width:700px\" /></p>\r\n</p>',25,'workFlow','2018-07-31 09:24:22','核准类账户变更流程图','核准类账户变更流程图'),(62,'/ckfinder/userfiles/result/104.png','<p><p><img alt=\"\" src=\"/ckfinder/userfiles/result/104.png\" style=\"height:420px; width:716px\" /></p>\r\n</p>',25,'workFlow','2018-07-31 09:26:00','核准类账户撤销流程图','核准类账户撤销流程图'),(63,'/ckfinder/userfiles/result/105.png','<p><p><img alt=\"\" src=\"/ckfinder/userfiles/result/105.png\" style=\"height:437px; width:708px\" /></p>\r\n</p>',25,'workFlow','2018-07-31 09:27:14','核准类账户开立流程图','核准类账户开立流程图'),(64,'/ckfinder/userfiles/result/106.png','<p><p><img alt=\"\" src=\"/ckfinder/userfiles/result/106.png\" style=\"height:446px; width:716px\" /></p>\r\n</p>',25,'workFlow','2018-07-31 09:29:04','开户许可证补（换）发业务流程图','开户许可证补（换）发业务流程图'),(65,'/ckfinder/userfiles/result/107.png','<p><p><img alt=\"\" src=\"/ckfinder/userfiles/result/107.png\" style=\"height:568px; width:458px\" /></p>\r\n</p>',19,'workFlow','2018-07-31 09:37:10','本人信用报告查询业务流程','本人信用报告查询业务流程'),(66,'/ckfinder/userfiles/result/108.png','<p><p><img alt=\"\" src=\"/ckfinder/userfiles/result/108.png\" style=\"height:516px; width:478px\" /></p>\r\n</p>',19,'workFlow','2018-07-31 09:39:27','企业信用报告查询业务流程','企业信用报告查询业务流程'),(68,'/ckfinder/userfiles/result/109.png','<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/ckfinder/userfiles/result/109.png\" style=\"height:679px; width:551px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/ckfinder/userfiles/images/110(1).png\" style=\"height:723px; width:551px\" /></p>\r\n',19,'workFlow','2018-07-31 09:44:15','征信投诉受理流程','征信投诉受理流程'),(69,'/ckfinder/userfiles/result/111.png','<p><p><img alt=\"\" src=\"/ckfinder/userfiles/result/111.png\" style=\"height:692px; width:550px\" /></p>\r\n</p>',19,'workFlow','2018-07-31 09:47:08','征信异议处理业务流程','征信异议处理业务流程'),(71,'/ckfinder/userfiles/result/112.png','<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/ckfinder/userfiles/images/123.png\" style=\"height:582px; width:457px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n',16,'workFlow','2018-07-31 09:54:01','内部审计工作运行流程图','内部审计工作运行流程图'),(74,'/ckfinder/userfiles/result/201807311359032.gif','<p><img src=\"/ckfinder/userfiles/result/201807311359032.gif\" /></p>\r\n',11,'workFlow','2018-07-31 13:59:37','党费的收缴与管理.gif','党费的使用审批流程.gif'),(75,'/ckfinder/userfiles/tmp/201807311402324.gif','<p><p><img src=\"/ckfinder/userfiles/tmp/201807311402324.gif\" /></p>\r\n</p>',11,'workFlow','2018-07-31 14:02:36','党费的收缴流程.gif','党费的收缴流程.gif'),(77,'/ckfinder/userfiles/result/201807311406015.gif','<p style=\"text-align:justify\">干部因公出国（境）管理流程</p>\r\n\r\n<p style=\"text-align:justify\">一、<strong>工作程序及要求</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.中心支行向天津分行报送正式请示;</p>\r\n\r\n<p style=\"text-align:justify\">2.天津分行党委组织部批复后，出国（境）人员到人事科领取出国（境）证件，办理出国（境）手续；</p>\r\n\r\n<p style=\"text-align:justify\">3.归国后10日内，出国（境）证件交人事科统一管理。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>二、呈报材料及要求</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>1.</strong><strong>地方组团：</strong>请示件正文。附：组团通知、出访目的、外方邀请函、出访日程、费用来源、因公临时出国（境）人员备案表等资料。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>2.</strong><strong>总行组团：</strong>请示件正文。附：总行出国（境）通知、因公临时出国（境）人员备案表等资料。</p>\r\n\r\n<p style=\"text-align:justify\">3.请示发文报天津分行人事处，电子版通过业务网邮箱上报天津分行人事处综合科。</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p><img src=\"/ckfinder/userfiles/result/201807311406015.gif\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n',11,'workFlow','2018-07-31 14:06:34','因公出国（境）管理工作流程.gif','因公出国（境）管理工作流程.gif'),(78,'/ckfinder/userfiles/tmp/201807311408226.gif','<p><h2 style=\"text-align: center;\">干部因私出国（境）管理流程</h2>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">一、<strong>工作程序及要求</strong></p>\r\n\r\n<p style=\"text-align:justify\">（一）个人递交因私出国（境）申请、填写《因私出国（境）审批表》。申请内容包括：个人基本情况，出访国、出访目的、时间和停留时间等；</p>\r\n\r\n<p style=\"text-align:justify\">（二）按照干部管理权限办理审批手续：</p>\r\n\r\n<p style=\"text-align:justify\">1.辖区支行行级领导申请出国（境）由支行向中心支行行文请示，其他工作人员申请出国（境）由支行自行审批；</p>\r\n\r\n<p style=\"text-align:justify\">2.副处级以上干部申请出国（境）由中心支行向天津分行人事处报送请示文件；中心支行机关其他工作人员由中心支行审核批准；</p>\r\n\r\n<p style=\"text-align:justify\">3.出国（境）申请批复后，中心支行机关工作人员到人事科领取出国（境）证件、县支行工作人员到综合办公室领取出国（境）证件办理出国（境）手续；</p>\r\n\r\n<p style=\"text-align:justify\">4.归国后10日内，中心支行机关工作人员将出国（境）证件交回人事科，县支行工作人员将出国（境）证件交回综合办公室统一管理。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>二、呈报材料及要求</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.个人提交申请：个人出国（境）申请书、填写《因私出国（境）审批表》。</p>\r\n\r\n<p style=\"text-align:justify\">2.需要向上级行报送请示的：请示件正文。附：个人出国（境）申请书、《因私出国（境）审批表》。</p>\r\n\r\n<p><img src=\"/ckfinder/userfiles/tmp/201807311408226.gif\" /></p>\r\n</p>',11,'workFlow','2018-07-31 14:09:08','因私出国（境）管理工作流程.gif','因私出国（境）管理工作流程.gif'),(79,'/ckfinder/userfiles/tmp/201807311421365.gif','<p><p style=\"text-align:justify\">行员年度考核工作程序</p>\r\n\r\n<p style=\"text-align:justify\">1.工作安排：中心支行成立年度行员考核领导小组，并对年度行员考核工作做出安排部署。</p>\r\n\r\n<p style=\"text-align:justify\">2.年度述职：被考核人员认真撰写年度述职报告，述职内容包括德、能、勤、绩、廉五个方面。德，主要指政治立场，思想作风，全局观念，法纪意识和职业道德。能，主要指学识水平，业务知识，政策水平，创新能力，决策能力，协调能力、领导艺术等方面的情况。勤，主要指敬业精神，工作态度，责任意识，遵守劳动纪律等方面的情况。绩，主要指年度内完成的工作质量和数量，效率、贡献及处理实际工作中遇到的疑难问题等方面的情况。廉，主要是指廉洁自律方面的情况。</p>\r\n\r\n<p style=\"text-align:justify\">机关正、副科长年度述职报告统一登载在中心支行OA网站上进行公示；中心支行工作人员述职以科室为单位，分管领导参加，进行全科述职。各支行组织进行支行内部述职工作。</p>\r\n\r\n<p style=\"text-align:justify\">3.民主测评：中心支行机关各科科长、副科长在机关副科级及以上干部范围内测评，其他工作人员在本科室范围内测评，分管领导参加科室测评投票。测评以无记名投票推荐方式进行。</p>\r\n\r\n<p style=\"text-align:justify\">4.等次建议：依据民主测评结果，科长、副科长由分管领导、科室由直接领导提出考核等次建议。年度考核分为A（优秀）、B（称职）、C（称职）、D（基本称职）、E（不称职）五个考核等次。</p>\r\n\r\n<p style=\"text-align:justify\">5.等次确定：中心支行人事科汇总考核等次建议，提请中心支行考核工作领导小组研究确定行员考核结果等次。</p>\r\n\r\n<p style=\"text-align:justify\">6.公示结果：人事部门对拟定优秀人员进行为期7个工作日的公示。公示结束期间，被考核人如果对考核结果持有异议，可向中心支行考核领导小组提出，对考核结果进行复议。</p>\r\n\r\n<p style=\"text-align:justify\">7.绩效面谈：公示结束期间后，由直接领导通过绩效面谈的形式向被考核行员进行反馈，客观评价被考核行员的工作表现。</p>\r\n\r\n<p style=\"text-align:justify\">8.填写行员考核表：被考核人员填写《中国人民银行分支行行员年度人员考核登记表》，A4纸打印后，由分管行领导和科室主要负责人审核签字后送交人事科，归入个人档案。</p>\r\n\r\n<p style=\"text-align:justify\">9.上报总结报告：将考核情况和考核结果形成报告，上报天津分行人事处，电子版传天津分行人事处综合科。</p>\r\n\r\n<p><img src=\"/ckfinder/userfiles/tmp/201807311421365.gif\" /></p>\r\n</p>',11,'workFlow','2018-07-31 14:22:01','工作人员年度行员考核工作流程.gif','工作人员年度行员考核工作流程.gif'),(80,'/ckfinder/userfiles/result/image(14).png','<p><p style=\"text-align:justify\">后备干部民主推荐工作流程</p>\r\n\r\n<p style=\"text-align:justify\"><strong>一、工作程序及要求</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.制定方案：依据上级行关于后备干部管理规定的选拔条件，制定本级后备干部选拔、推荐、培养管理工作方案，经党委研究同意后组织实施。</p>\r\n\r\n<p style=\"text-align:justify\">2.民主推荐：召开后备干部人选民主推荐大会，进行民主推荐。参加推荐正、副处级领导职务后备干部的人员范围：中心支行机关副科级以上干部、辖区各支行主要负责人。</p>\r\n\r\n<p style=\"text-align:justify\">3.党委建议：在民主推荐的基础上，综合分析民主推荐结果，并根据后备干部推荐的条件、数量和结构要求，召开党委会议集体研究，按照推荐比例提出后备干部建议人选名单，报天津分行党委。</p>\r\n\r\n<p style=\"text-align:justify\">4.组织考察认定：正处级领导职务后备人选由天津分行党委组织部在一定范围内采取个别谈话的形式进行组织考察，提出建议人选名单，报分行党委研究认定；副处级领导职务后备人选分别由分行党委组织部和中心支行党委组织部差额考察认定。</p>\r\n\r\n<p style=\"text-align:justify\">5.书面报告：认定后备人选后形成书面报告，上报天津分行党委。并以适当的方式反馈给支行党组。</p>\r\n\r\n<p style=\"text-align:justify\">6.组织培养：根据培养计划对后备干部有计划、有目的地进行培养。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>二、呈报材料及要求</strong></p>\r\n\r\n<p style=\"text-align:justify\">党委报告。附：党委会议记录（复印件），后备干部建议人选名单，民主推荐情况汇总统计表、后备干部简要情况登记表，每一个后备干部的考察材料。考察材料包括：一是&ldquo;德、能、勤、绩、廉&rdquo;方面的主要表现和主要特长；二是主要缺点和不足；三是民主推荐和民主测评。考察材料尽可能简要，字数控制在1200字左右。考察中发现的重大问题的专项调查材料及其他补充说明材料，可作为附件后附。</p>\r\n\r\n<p style=\"text-align:justify\"><img src=\"/ckfinder/userfiles/result/image(14).png\" style=\"height:457px; width:310px\" /></p>\r\n</p>',11,'workFlow','2018-07-31 14:23:30','后备干部民主推荐工作流程','后备干部民主推荐工作流程'),(82,'/ckfinder/userfiles/result/image(14).png','<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">组织部长、人事科长职务任免</p>\r\n\r\n<p style=\"text-align:justify\"><strong>一、工作程序及要求</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.中心支行党委会议研究拟定人选；</p>\r\n\r\n<p style=\"text-align:justify\">2.中心支行党委主要负责同志与分行党委组织部主要负责同志进行沟通；</p>\r\n\r\n<p style=\"text-align:justify\">3.拟提拔任职的，中心支行党委按照有关规定组织选拔工作，任前公示结束后向天津分行党委组织部报送正式请示文件；拟平职调整的，向分行党委组织部报送正式请示文件；</p>\r\n\r\n<p style=\"text-align:justify\">4.天津分行党委组织部派人对拟任人选组织考察；</p>\r\n\r\n<p style=\"text-align:justify\">5.天津分行党委组织部行文批复后，中心支行党委组织部按照干部管理权限办理有关手续。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>二、呈报材料及要求</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>1.</strong><strong>呈报材料：</strong>请示件正文。附：干部任免审批表；提拔考察材料或平职调整推荐材料；党委会议记录、党委会议纪要。报件一式3份，材料用A4纸打印。电子版通过业务网邮箱上报天津分行党委组织部干部科。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>2.</strong><strong>文件要求：</strong>请示行文标题为《关于XX、XX同志职务任免的请示》。主送单位为天津分行党委组织部，落款为中心支行党委。正文主要包括有关人选职务任免理由、拟任人选的情况。提拔任职的，还应该简要说明人选民主推荐、考察和公示情况等。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>三、任免职程序及要求</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>1.</strong><strong>任职发文：</strong>以&ldquo;巴银党任&rdquo;文种任免党委组织部部长职务，以&ldquo;巴银任&rdquo;文种任免人事科科长职务。任免职文件抄送天津分行党委组织部（人事科）；</p>\r\n\r\n<p style=\"text-align:justify\"><strong>2.</strong><strong>变更信息：</strong>人事科按照有关政策规定，变更任免职人员的干部档案、劳动工资、养老统筹等信息，并通知财务部门从任职的下月份起执行新任免职务的工资和养老统筹缴费。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>3.</strong><strong>归档管理：</strong>将上报天津分行党委组织部的请示文件、天津分行党委组织部的批复，以及中心支行的任免文件整理归入文书档案；干部任免审批表、工资审批表等整理归入任职人员的个人档案。</p>\r\n\r\n<p style=\"text-align:justify\"><img src=\"/ckfinder/userfiles/result/image(14).png\" style=\"height:601px; width:576px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n',11,'workFlow','2018-07-31 14:29:01','组织部长、人事科长职务任免','干部交流1-人事'),(83,'/ckfinder/userfiles/result/image(14).png','<p><p style=\"text-align:justify\">纪委副书记、纪委监察室主任职务任免</p>\r\n\r\n<p style=\"text-align:justify\"><strong>一、工作流程</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.中心支行党委会议研究拟定人选（纪委副书记原则上从正科长干部中拟定）；</p>\r\n\r\n<p style=\"text-align:justify\">2.中心支行党委行文向天津分行纪委请示（拟提拔任职，一般应在任前公示后上报请示）；</p>\r\n\r\n<p style=\"text-align:justify\">4.天津分行纪委批复后，中心支行党委行文向天津分行党委组织部请示；</p>\r\n\r\n<p style=\"text-align:justify\">5.天津分行党委组织部行文批复后，中心支行党委组织部按照干部管理权限办理有关任职手续。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>二、呈报材料及要求</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>（一）向天津分行纪委呈报</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.请示件正文。附件：无。</p>\r\n\r\n<p style=\"text-align:justify\">2.文件要求：请示行文标题为《关于XX、XX同志拟任免XXX职务的请示》。主送单位为天津分行纪委，落款为中心支行党委并盖章。正文主要包括拟任人选的个人情况、选任理由及是否胜任职务要求等内容。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>（二）向天津分行党委组织部呈报</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.呈报材料：请示件正文。附件：干部任免审批表（一式3份），提拔考察材料（平职调整材料可不附），党委会议记录、党委会议纪要，天津分行纪委批复。材料用A4纸打印。电子版通过业务网邮箱上报天津分行党委组织部干部科。</p>\r\n\r\n<p style=\"text-align:justify\">2.文件要求：请示行文标题为《关于XX、XX同志职务任免的请示》。主送单位为天津分行党委组织部，落款为中心支行党委。正文主要包括有关人选职务任免理由、拟任人选的情况。提拔任职的，还应该简要说明人选民主推荐、考察和公示情况等。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>三、任免职程序及要求</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>1.</strong><strong>任免职发文</strong><strong>：</strong>以&ldquo;巴银党任&rdquo;文种任免职务。任免职文件抄送天津分行党委组织部、天津分行纪委监察室；</p>\r\n\r\n<p style=\"text-align:justify\"><strong>2.</strong><strong>变更信息</strong><strong>：</strong>人事科按照有关政策规定，变更任免职人员的干部档案、劳动工资、养老统筹等信息，并通知财务部门从任职的下月份起执行新任免职务的工资和养老统筹缴费。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>3.</strong><strong>归档管理</strong><strong>：</strong>将上报天津分行纪委的请示和批复、上报天津分行党委组织部的请示文件和批复，以及中心支行的任免文件等整理归入文书档案；干部任免审批表、工资审批表等整理归入任职人员的个人档案。</p>\r\n\r\n<p style=\"text-align:justify\"><img src=\"/ckfinder/userfiles/result/image(14).png\" style=\"height:672px; width:512px\" /></p>\r\n</p>',11,'workFlow','2018-07-31 14:30:09','干部交流2-纪委','干部交流2-纪委'),(84,'/ckfinder/userfiles/result/image(14).png','<p><p style=\"text-align:justify\">内审科科长职务任免</p>\r\n\r\n<p style=\"text-align:justify\"><strong>一、工作流程</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.中心支行党委会议研究拟定人选；</p>\r\n\r\n<p style=\"text-align:justify\">2.中心支行党委向天津分行党委组织部报送正式请示文件（拟提拔任职，一般应在任前公示后上报请示）；</p>\r\n\r\n<p style=\"text-align:justify\">3.分行党委组织部行文批复后，中心支行党委组织部按照干部管理权限办理有关任职手续。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>二、呈报材料及要求</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>1.</strong><strong>呈报材料</strong>：请示件正文。附：干部任免审批表；提拔考察材料（平职调整材料可不附）；党委会议记录、党委会议纪要。报件一式3份，材料用A4纸打印。电子版通过业务网邮箱上报天津分行党委组织部干部科。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>2.</strong><strong>文件要求</strong>：请示行文标题为《关于XX、XX同志职务任免的请示》。主送单位为天津分行党委组织部，落款为中心支行党委。正文主要包括有关人选职务任免理由、拟任人选的情况。提拔任职的，还应该简要说明人选民主推荐、考察和公示情况等。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>三、任免职程序及要求</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>1.</strong><strong>任职发文</strong><strong>：</strong>以&ldquo;巴银任&rdquo;文种任免内审科科长职务。任免职文件抄送天津分行人事处；</p>\r\n\r\n<p style=\"text-align:justify\"><strong>2.</strong><strong>变更信息</strong><strong>：</strong>人事科按照有关政策规定，变更任免职人员的干部档案、劳动工资、养老统筹等信息，并通知财务部门从任职的下月份起执行新任免职务的工资和养老统筹缴费。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>3.</strong><strong>归档管理</strong><strong>：</strong>将上报天津分行党委组织部的请示文件、天津分行党委组织部的批复，以及中心支行的任免文件整理归入文书档案；干部任免审批表、工资审批表等整理归入任职人员的个人档案。</p>\r\n\r\n<p style=\"text-align:justify\"><img src=\"/ckfinder/userfiles/result/image(14).png\" style=\"height:610px; width:517px\" /></p>\r\n</p>',11,'workFlow','2018-07-31 14:31:22','干部交流3-内审','干部交流3-内审'),(85,'/ckfinder/userfiles/result/image(14).png','<p><p style=\"text-align:justify\">会计财务科科长职务任免</p>\r\n\r\n<p style=\"text-align:justify\"><strong>一、工作流程</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.中心支行党委会议研究拟定人选；</p>\r\n\r\n<p style=\"text-align:justify\">2.中心支行党委主要负责同志就拟任人选与呼和浩特中心支行会计财务处主要负责同志沟通；</p>\r\n\r\n<p style=\"text-align:justify\">3.中心支行就拟任人选任职资格向呼和浩特中心支行会计财务处行文请示（拟提拔任职，一般应在任前公示后上报请示）。</p>\r\n\r\n<p style=\"text-align:justify\">4.呼和浩特中心支行会计财务处行文批复后，向天津分行党委组织部报送正式请示文件；</p>\r\n\r\n<p style=\"text-align:justify\">5.分行党委组织部行文批复后，中心支行党委组织部按照批复意见办理有关手续。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>二、呈报材料及要求</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>（一）呈报呼和浩特中心支行</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.呈报材料：请示件正文。附：《中国人民银行会计（营业）部门主管人员任职资格申报审查表》（一式3份），材料用A4纸打印。</p>\r\n\r\n<p style=\"text-align:justify\">2.文件要求：请示标题为《关于XX拟任会计财务科科长资格审查的请示》。主送单位为呼和浩特中心支行，落款为中心支行并盖章。正文主要包括拟任人选的个人及家庭情况、选任理由及是否胜任职务要求等内容。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>（二）呈报天津分行党委组织部</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>1.</strong><strong>呈报材料</strong>：请示件正文。附：干部任免审批表；提拔考察材料（平职调整材料可不附）；经呼和浩特中心支行会计财务处审批后的《中国人民银行会计（营业）部门主管人员任职资格申报审查表》；党委会议记录、党委会议纪要。报件一式3份，材料用A4纸打印。电子版通过业务网邮箱上报天津分行党委组织部干部科。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>2.</strong><strong>文件要求</strong>：请示行文标题为《关于XX、XX同志职务任免的请示》。主送单位为天津分行党委组织部，落款为中心支行党委。正文主要包括有关人选职务任免理由、拟任人选的基本情况。提拔任职的，还应该简要说明人选民主推荐、考察和公示情况等。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>三、任免职程序及要求</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>1.</strong><strong>任职发文</strong><strong>：</strong>以&ldquo;巴银任&rdquo;文种任免会计财务科科长职务。任免职文件抄送天津分行人事处；</p>\r\n\r\n<p style=\"text-align:justify\"><strong>2.</strong><strong>变更信息</strong><strong>：</strong>人事科按照有关政策规定，变更任免职人员的干部档案、劳动工资、养老统筹等信息，并通知财务部门从任职的下月份起执行新任免职务的工资和养老统筹缴费。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>3.</strong><strong>归档管理</strong><strong>：</strong>将上报呼和浩特中心支行的请示、呼和浩特中心支行批复、天津分行党委组织部的请示文件、天津分行党委组织部的批复，以及中心支行的任免文件整理归入文书档案；干部任免审批表、《中国人民银行会计（营业）部门主管人员任职资格申报审查表》、工资审批表等整理归入任职人员的个人档案。</p>\r\n\r\n<p style=\"text-align:justify\"><img src=\"/ckfinder/userfiles/result/image(14).png\" style=\"height:602px; width:544px\" /></p>\r\n</p>',11,'workFlow','2018-07-31 14:32:38','干部交流4-会计','干部交流4-会计'),(86,'/ckfinder/userfiles/result/image(14).png','<p><p style=\"text-align:justify\">其他科级干部调整交流</p>\r\n\r\n<p style=\"text-align:justify\"><strong>一、工作流程</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.中心支行党委会议研究拟定调整交流人选；</p>\r\n\r\n<p style=\"text-align:justify\">2.拟提拔交流的人员，在原单位（部门）公示后，中心支行党委组织部行文任职文件；平职调整的，直接行文任免职文件；</p>\r\n\r\n<p style=\"text-align:justify\">3.按照干部管理权限办理有关任免职手续，并向天津分行报备。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>二、任免职程序及要求</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>（一）任免职发文</strong><strong>：</strong>中心支行党委职能部门（党委办公室、党委组织部、宣传群工部、纪委监察室）负责人，支行党组成员的职务变动以&ldquo;巴银党任&rdquo;文种任免；中心支行职能科室的正副科长（主任）、支行领导班子成员的职务变动以&ldquo;巴银任&rdquo;文种任免。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>（二）变更信息</strong><strong>：</strong>人事科按照有关政策规定，变更任免职人员的干部档案、劳动工资、养老统筹等信息，并通知财务部门从任职的下月份起执行新任免职务的工资和养老统筹缴费。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>（三）归档管理</strong><strong>：</strong>将中心支行的任免文件整理归入文书档案；干部任免审批表、工资审批表等整理归入任职人员的个人档案。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>（四）任免职事后报备</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>1.</strong><strong>报备范围</strong><strong>：</strong>中心支行党委各部、室部长（主任），各科室科长（主任）；党委组织部副部长、人事科副科长，内审科副科长，会计财务科副科长；辖区各支行党组书记、行长。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>2.</strong><strong>报备材料</strong><strong>：</strong>干部任免文件，干部任免审批表。报件一式1份，材料用A4纸打印。上报天津分行党委组织部干部科。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>3.</strong><strong>报备时限：</strong>任免职文件下达10日内报备。</p>\r\n\r\n<p style=\"text-align:justify\"><img src=\"/ckfinder/userfiles/result/image(14).png\" style=\"height:476px; width:468px\" /></p>\r\n</p>',11,'workFlow','2018-07-31 14:33:45','干部交流5-调整','干部交流5-调整'),(89,'/ckfinder/userfiles/result/image(14).png','<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>干部人事档案管理流程</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>一、新增档案</strong></p>\r\n\r\n<p style=\"text-align:justify\">（一）核对接受：对新增档案认真检查其档案目录与档案内容是否一致，如缺少材料，及时与有关部门联系，进行补充，无误后接收，开具回执。</p>\r\n\r\n<p style=\"text-align:justify\">（二）登记入库：将新增档案及时记入接受档案登记簿和人事档案名册。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>二、收集归档</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>（一）</strong>收集核对：严格按照《档案收集归档制度》规定的内容定期向档案材料形成单位和部门收集档案材料。</p>\r\n\r\n<p style=\"text-align:justify\">（二）鉴别归档：检查收集归档材料是否符合归档要求，鉴别无误后，及时归档。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>三、调出档案：</strong></p>\r\n\r\n<p style=\"text-align:justify\">（一）审查调档手续：认真审核干部调出档案有关手续。</p>\r\n\r\n<p style=\"text-align:justify\">（二）核对档案：认真核对档案材料内容，确保转出档案材料内容完整。</p>\r\n\r\n<p style=\"text-align:justify\">（三）办理手续：开具&ldquo;干部档案材料转递通知单&rdquo;，作好记载，以便备查。</p>\r\n\r\n<p style=\"text-align:justify\">（四）收取回执：对转出档案要及时收取回执，妥善保管。</p>\r\n\r\n<p style=\"text-align:justify\"><img src=\"/ckfinder/userfiles/result/image(14).png\" style=\"height:551px; width:642px\" /></p>\r\n\r\n<p style=\"text-align:justify\"><img src=\"/ckfinder/userfiles/images/image(14).png\" style=\"height:407px; width:524px\" /></p>\r\n\r\n<p style=\"text-align:justify\"><img src=\"/ckfinder/userfiles/images/image(17).png\" style=\"height:411px; width:568px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n',11,'workFlow','2018-07-31 14:38:27','人事档案工作管理流程','人事档案工作管理流程'),(90,'/ckfinder/userfiles/result/image(18).png','<p><p style=\"text-align:justify\">支行行（科）级领导选拔任用工作流程</p>\r\n\r\n<p style=\"text-align:justify\"><strong>一、工作程序及要求</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>（一）动议</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.根据工作需要和支行领导班子、干部队伍建设情况，由党委（或组织人事部门）提出干部选拔任用工作意见；</p>\r\n\r\n<p style=\"text-align:justify\">2.组织人事部门就选拔任用的职位、条件、范围、方式、程序提出初步建议；</p>\r\n\r\n<p style=\"text-align:justify\">3.初步建议经党委主要负责人同意后，在一定范围内酝酿，形成工作方案；</p>\r\n\r\n<p style=\"text-align:justify\"><strong>（二）民主推荐</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.会议推荐。由支行组织召开推荐会，参加会议推荐人员范围为支行行级领导、科级非领导、股级干部及其他需要参加的人员。上述推荐范围人数较少的，可根据知情度、关联度和代表性原则适当扩大范围。</p>\r\n\r\n<p style=\"text-align:justify\">由中心支行组织人事部门公布推荐职位、任职条件、推荐范围，提供干部名册，提出推荐要求，组织填写推荐表；</p>\r\n\r\n<p style=\"text-align:justify\">2.个别谈话推荐。个别谈话范围参照会议推荐范围，也可适当调整。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>（三）考察</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.确定考察对象。考察对象由中心支行党委研究确定。对民主推荐意见比较集中的，可进行等额考察；意见不集中的，应当进行差额考察。</p>\r\n\r\n<p style=\"text-align:justify\">2.成立考察组，制定考察工作方案。考察组由两名以上成员组成。考察组应与支行党组主要负责人就考察工作方案沟通情况，征求意见。</p>\r\n\r\n<p style=\"text-align:justify\">3.发布干部考察预告。在考察对象所在支行发布干部考察预告。</p>\r\n\r\n<p style=\"text-align:justify\">4.考察方式。采取个别谈话、发放征求意见表、民主测评、实地走访、查阅干部档案和工作资料、同考察对象面谈。</p>\r\n\r\n<p style=\"text-align:justify\">个别谈话和征求意见范围为：中心支行行领导、支行班子成员、支行科级非领导、支行股级干部及其他相关人员。</p>\r\n\r\n<p style=\"text-align:justify\">民主测评范围为：考察对象所在部门工作人员，参加人数不得少于应参加人数的80％。</p>\r\n\r\n<p style=\"text-align:justify\">5.征求纪检监察部门意见。由支行纪检监察部门出具书面意见。</p>\r\n\r\n<p style=\"text-align:justify\">6.任前档案审核。</p>\r\n\r\n<p style=\"text-align:justify\">7.形成考察材料。已经任职的，书面考察材料归入本人档案。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>（四）讨论决定</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.召开党委会讨论。党委会必须有三分之二以上成员到会。</p>\r\n\r\n<p style=\"text-align:justify\">2.汇报推荐考察情况。组织人事部门主要负责人应逐个全面、准确、清楚地介绍所讨论干部人选的推荐、考察情况。</p>\r\n\r\n<p style=\"text-align:justify\">2.形成任免决定。党委会到会成员应对任免事项逐一发表&ldquo;同意&rdquo;、&ldquo;不同意&rdquo;或&ldquo;缓议&rdquo;等明确意见。在充分讨论的基础上，通过口头表决、举手表决或无记名投票方式进行表决。以应到会成员过半数同意形成任免决定。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>（五）任职</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.任职方式。领导干部任职主要实行委任制，对工人身份的人员选拔为领导干部的，实行聘任制。</p>\r\n\r\n<p style=\"text-align:justify\">2.任前公示。公示期不少于5个工作日，从发布公示通知的第二天算起，第二天为节假日的，以节假日后的第一个工作日算起。</p>\r\n\r\n<p style=\"text-align:justify\">3.任职。公示结果不影响任职的，公示期结束后由组织人事部门办理任职手续。</p>\r\n\r\n<p style=\"text-align:justify\">4.任职谈话。由中心支行党委指定专人同本人谈话，肯定成绩，指出不足，提出要求和注意的问题。</p>\r\n\r\n<p style=\"text-align:justify\">5.任职时间计算。领导干部任职时间自党委决定任职之日起计算。</p>\r\n\r\n<p style=\"text-align:justify\">6.试用期制度。试用期一年，从中心支行发出试用任职通知起计算。干部任职试用期间，一般不调整其工作岗位。</p>\r\n\r\n<p style=\"text-align:justify\">7.正式任职。试用期满后，经考核胜任现职的，正式任职。试用期计入任职时间。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>&nbsp;&nbsp;&nbsp; </strong><strong>二、任职程序及要求</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>1.</strong><strong>任免职发文</strong><strong>：</strong>中心支行党委职能部门（党委办公室、党委组织部、宣传群工部、纪委监察室）负责人，支行党组成员的职务变动以&ldquo;乌银党任&rdquo;文种任免；中心支行职能科室的正、副科长（主任）、支行领导班子成员的职务变动以&ldquo;巴银任&rdquo;文种任免。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>2.</strong><strong>变更信息</strong><strong>：</strong>中心支行人事科按照有关政策规定，变更任免职人员的干部档案、人力资源系统信息、养老统筹等信息等。并通知财务部门从任职的下月份起执行新任免职务的工资和养老统筹缴费。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>3.</strong><strong>归档管理</strong><strong>：</strong>将中心支行的任免文件整理归入文书档案；干部任免审批表、工资审批表、考察材料等整理归入任职人员的个人档案。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>&nbsp;&nbsp;&nbsp; </strong><strong>三、任前需请示上级行批复</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>请示范围</strong><strong>：</strong>中心支行党委组织部长、人事科科长，纪委副书记、纪委监察室主任，内审科科长，会计财务科科长。（请示及要求见相关流程）</p>\r\n\r\n<p style=\"text-align:justify\"><strong>&nbsp;&nbsp;&nbsp; </strong><strong>四、任免职事后报备</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>1.</strong><strong>报备范围</strong><strong>：</strong>中心支行党委各部、室部长（主任），各科室科长（主任）；党委组织部副部长、人事科副科长，内审科副科长，会计财务科副科长；辖区各支行党组书记、行长。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>2.</strong><strong>报备材料</strong><strong>：</strong>干部任免文件，干部任免审批表一式一份，A4纸打印，上报天津分行党委组织部干部科。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>3.</strong><strong>报备时限：</strong>任免职文件下达10日内报备。</p>\r\n\r\n<p style=\"text-align:justify\"><img src=\"/ckfinder/userfiles/result/image(18).png\" style=\"height:789px; width:550px\" /></p>\r\n</p>',11,'workFlow','2018-07-31 14:44:35','干部提拔1-支行','干部提拔1-支行'),(91,'/ckfinder/userfiles/result/image(18).png','<p><p style=\"text-align:justify\">科级非领导干部选拔任用工作流程</p>\r\n\r\n<p style=\"text-align:justify\"><strong>一、工作程序及要求</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>（一）动议</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.根据工作需要、干部队伍建设和机关非领导职数空缺情况，由党委（或组织人事部门）提出非领导干部选拔任用工作意见；</p>\r\n\r\n<p style=\"text-align:justify\">2.由领导职务改任同级非领导职务，不进行选拔等干部考察任用程序；</p>\r\n\r\n<p style=\"text-align:justify\">3.新录用行员、外单位调入人员以及军转干部等初次任非领导职务的，应当根据其学历、经历、原任职务并结合本单位的工作需要、职数等情况，确定其非领导职务的级别。</p>\r\n\r\n<p style=\"text-align:justify\">4.组织人事部门就选拔任用的职位、条件、范围、方式、程序提出初步建议；</p>\r\n\r\n<p style=\"text-align:justify\">5.初步建议经党委主要负责人同意后，在一定范围内酝酿，形成选拔方案。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>（二）资格审查</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.公布方案。召开党委会议，审议并通过非领导职务选拔实施方案。在机关公布推荐选拔工作实施方案，公布推荐职位、任职条件、推荐范围，提供干部名册，提出推荐要求，并明确工作程序、工作要求及工作纪律；</p>\r\n\r\n<p style=\"text-align:justify\">2.个人报名。按照非领导职务规定的条件，由个人填写《非领导职务推荐选拔申报表》；</p>\r\n\r\n<p style=\"text-align:justify\">3.资格审查。由中心支行组织人事部门按照条件规定，逐一审查报名人员的任职资格，审查结束后，在机关范围内对审查合格人员进行公示；</p>\r\n\r\n<p style=\"text-align:justify\"><strong>（三）民主推荐</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.会议推荐。由中心支行组织召开推荐会。中心支行机关参加推荐会议人员范围为中心支行行级领导、机关副科级以上干部。支行参加推荐会议人员为支行行级领导、股级以上干部。参加人员必须达到应参加人数的三分之二。</p>\r\n\r\n<p style=\"text-align:justify\">由组织人事部门公布推荐职位，提供干部名册，提出推荐要求，组织填写推荐表；</p>\r\n\r\n<p style=\"text-align:justify\">2.个别谈话推荐。个别谈话范围为机关各科室科长。</p>\r\n\r\n<p style=\"text-align:justify\">3.考察对象由中心支行党委研究确定。根据工作需要和干部德才条件，结合平时考核、年终考核、一贯表现、人岗相适和民主推荐等情况综合考虑，充分酝酿，确定组织考察对象。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>（三）考察</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.考察对象由中心支行党委研究确定。根据工作需要和干部德才条件，结合平时考核、年终考核、一贯表现、人岗相适和民主推荐等情况综合考虑，充分酝酿，确定组织考察对象。</p>\r\n\r\n<p style=\"text-align:justify\">2.中心支行成立考察组，制定考察工作方案。考察组由两名以上成员组成。组织考察内容主要包括审核档案、个别谈话、民主测评、与考核对象面谈。</p>\r\n\r\n<p style=\"text-align:justify\">①　按照《中国人民银行干部任前档案审核办法》，对考察对象个人档案进行严格审核，重点审核&ldquo;三龄二历一身份&rdquo;，以及家庭成员信息等内容，必要时进行核实。</p>\r\n\r\n<p style=\"text-align:justify\">②　个别谈话主要围绕德、能、勤、绩、廉方面的主要表现和主要特长听取对考察对象的意见。谈话范围是考察对象所在科室的全体工作人员、机关各科室主要负责人。</p>\r\n\r\n<p style=\"text-align:justify\">③　民主测评在考察对象所在科室进行，科内全体人员参加。参加人数不得少于应参加人数的80％。</p>\r\n\r\n<p style=\"text-align:justify\">④　与考察对象面谈。深入了解考察对象的思想状况、沟通能力、应变能力等方面的情况。</p>\r\n\r\n<p style=\"text-align:justify\">3.征求纪检监察部门意见。由支行纪检监察部门出具书面意见。</p>\r\n\r\n<p style=\"text-align:justify\">4.考察组形成组织考察材料，向中心支行党委汇报。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>（四）讨论决定</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.召开党委会讨论。党委会必须有三分之二以上成员到会。</p>\r\n\r\n<p style=\"text-align:justify\">2.汇报推荐考察情况。组织人事部门主要负责人应逐个全面、准确、清楚地介绍所讨论干部人选的推荐、考察情况。</p>\r\n\r\n<p style=\"text-align:justify\">2.形成任免决定。党委会到会成员应对任免事项逐一发表&ldquo;同意&rdquo;、&ldquo;不同意&rdquo;或&ldquo;缓议&rdquo;等明确意见。在充分讨论的基础上，通过口头表决、举手表决或无记名投票方式进行表决。以应到会成员过半数同意形成任免决定。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>（五）任职</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.任职方式。非领导干部任职实行委任制。</p>\r\n\r\n<p style=\"text-align:justify\">2.任前公示。公示期不少于5个工作日，从发布公示通知的第二天算起，第二天为节假日的，以节假日后的第一个工作日算起。</p>\r\n\r\n<p style=\"text-align:justify\">3.任职谈话。由中心支行党委指定专人同本人谈话，肯定成绩，指出不足，提出要求和注意的问题。</p>\r\n\r\n<p style=\"text-align:justify\">4.任职。公示结果不影响任职的，公示期结束后由组织人事部门办理任职手续（需任前向上级行请示批复的，待批复下达后办理任职手续）。</p>\r\n\r\n<p style=\"text-align:justify\">5.任职时间计算。非领导干部任职时间自党委决定任职之日起计算。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>二、任职程序及要求</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>1.</strong><strong>任免职发文</strong><strong>：</strong>非领导职务变动以&ldquo;巴银任&rdquo;文种任免。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>2.</strong><strong>变更信息</strong><strong>：</strong>中心支行人事科按照有关政策规定，变更任免职人员的干部档案、人力资源系统信息、养老统筹等信息等。并通知财务部门从任职的下月份起执行新任免职务的工资和养老统筹缴费。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>3.</strong><strong>归档管理</strong><strong>：</strong>将中心支行的任免文件整理归入文书档案；干部任免审批表、工资审批表、考察材料等整理归入任职人员的个人档案。<strong>&nbsp; </strong></p>\r\n\r\n<p style=\"text-align:justify\"><img src=\"/ckfinder/userfiles/result/image(18).png\" style=\"height:818px; width:581px\" /></p>\r\n</p>',11,'workFlow','2018-07-31 14:46:32','干部提拔2-非领导','干部提拔2-非领导'),(92,'/ckfinder/userfiles/result/image(18).png','<p><p style=\"text-align:justify\">机关科级领导干部选拔拔任用工作流程</p>\r\n\r\n<p style=\"text-align:justify\"><strong>一、工作程序及要求</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>（一）动议&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<p style=\"text-align:justify\">1.根据工作需要和中心支行机关科室和干部队伍建设情况，由党委（或组织人事部门）提出干部选拔任用工作意见；</p>\r\n\r\n<p style=\"text-align:justify\">2.组织人事部门就选拔任用的职位、条件、范围、方式、程序提出初步建议；</p>\r\n\r\n<p style=\"text-align:justify\">3.初步建议经党委主要负责人同意后，在一定范围内酝酿，形成选拔方案；</p>\r\n\r\n<p style=\"text-align:justify\">4.如一次选拔10人及以上的，选拔方案上报天津分行党委组织部审核批准。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>（二）民主推荐</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.会议推荐。由中心支行组织召开推荐会。推荐参加会议推荐人员范围为中心支行行级领导、机关副科级以上干部。参加人员必须达到应参加人数的三分之二。</p>\r\n\r\n<p style=\"text-align:justify\">由中心支行组织人事部门公布推荐职位、任职条件、推荐范围，提供干部名册，提出推荐要求，组织填写推荐表；</p>\r\n\r\n<p style=\"text-align:justify\">2.个别谈话推荐。个别谈话范围为机关各科室科长。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>（三）确定人选</strong></p>\r\n\r\n<p style=\"text-align:justify\">考察对象由中心支行党委研究确定。根据工作需要和干部德才条件，结合平时考核、年终考核、一贯表现、人岗相适和民主推荐等情况综合考虑，充分酝酿，确定组织考察对象。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>（四）考察</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.成立考察组，制定考察工作方案。考察组由两名以上成员组成。对民主推荐意见比较集中的，可进行等额考察；意见不集中的，应当进行差额考察。</p>\r\n\r\n<p style=\"text-align:justify\">2.组织考察内容：主要包括审核档案、个别谈话、民主测评、与考核对象面谈。</p>\r\n\r\n<p style=\"text-align:justify\">①　按照《中国人民银行干部任前档案审核办法》，对考察对象个人档案进行严格审核，重点审核&ldquo;三龄二历一身份&rdquo;，以及家庭成员信息等内容，必要时进行核实。</p>\r\n\r\n<p style=\"text-align:justify\">②　个别谈话主要围绕德、能、勤、绩、廉方面的主要表现和主要特长听取对考察对象的意见。谈话范围是考察对象所在科室的全体工作人员、机关各科室主要负责人。</p>\r\n\r\n<p style=\"text-align:justify\">③　民主测评在考察对象所在科室进行，科内全体人员参加。参加人数不得少于应参加人数的80％。</p>\r\n\r\n<p style=\"text-align:justify\">④　与考察对象面谈。深入了解考察对象的思想状况、沟通能力、应变能力等方面的情况。</p>\r\n\r\n<p style=\"text-align:justify\">3.征求纪检监察部门意见。由支行纪检监察部门出具书面意见。</p>\r\n\r\n<p style=\"text-align:justify\">4.考察组形成组织考察材料，向中心支行党委汇报。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>（五）讨论决定</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.召开党委会讨论。党委会必须有三分之二以上成员到会。</p>\r\n\r\n<p style=\"text-align:justify\">2.汇报推荐考察情况。组织人事部门主要负责人应逐个全面、准确、清楚地介绍所讨论干部人选的推荐、考察情况。</p>\r\n\r\n<p style=\"text-align:justify\">2.形成任免决定。党委会到会成员应对任免事项逐一发表&ldquo;同意&rdquo;、&ldquo;不同意&rdquo;或&ldquo;缓议&rdquo;等明确意见。在充分讨论的基础上，通过口头表决、举手表决或无记名投票方式进行表决。以应到会成员过半数同意形成任免决定。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>（六）任职</strong></p>\r\n\r\n<p style=\"text-align:justify\">1.任职方式。选拔干部任职主要实行委任制，对工人身份的人员选拔为领导干部的，实行聘任制。</p>\r\n\r\n<p style=\"text-align:justify\">2.任前公示。公示期不少于5个工作日，从发布公示通知的第二天算起，第二天为节假日的，以节假日后的第一个工作日算起。</p>\r\n\r\n<p style=\"text-align:justify\">3.任职谈话。由中心支行党委指定专人同本人谈话，肯定成绩，指出不足，提出要求和注意的问题。</p>\r\n\r\n<p style=\"text-align:justify\">4.任职。公示结果不影响任职的，公示期结束后由组织人事部门办理任职手续（需任前向上级行请示批复的，待批复下达后办理任职手续）。</p>\r\n\r\n<p style=\"text-align:justify\">5.任职时间计算。干部任职时间自党委决定任职之日起计算。</p>\r\n\r\n<p style=\"text-align:justify\">6.试用期制度。试用期一年，从中心支行发出试用任职通知起计算。干部任职试用期间，一般不调整其工作岗位。试用期满后，经考核胜任现职的，正式任职。试用期计入任职时间。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>二、任职程序及要求</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>1.</strong><strong>任免职发文</strong><strong>：</strong>中心支行党委职能部门（党委办公室、党委组织部、宣传群工部、纪委监察室）负责人，支行党组成员的职务变动以&ldquo;巴银党任&rdquo;文种任免；中心支行职能科室的正、副科长（主任）、支行领导班子成员等职务变动以&ldquo;巴银任&rdquo;文种任免。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>2.</strong><strong>变更信息</strong><strong>：</strong>中心支行人事科按照有关政策规定，变更任免职人员的干部档案、人力资源系统信息、养老统筹等信息等。并通知财务部门从任职的下月份起执行新任免职务的工资和养老统筹缴费。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>3.</strong><strong>归档管理</strong><strong>：</strong>将中心支行的任免文件整理归入文书档案；干部任免审批表、工资审批表、考察材料等整理归入任职人员的个人档案。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>&nbsp;&nbsp;&nbsp; </strong><strong>三、任前需请示上级行批复</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>请示范围</strong><strong>：</strong>中心支行党委组织部长、人事科科长，纪委副书记、纪委监察室主任，内审科科长，会计财务科科长。（请示及要求见相关流程）</p>\r\n\r\n<p style=\"text-align:justify\"><strong>&nbsp;&nbsp;&nbsp; </strong><strong>四、任免职事后报备</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>1.</strong><strong>报备范围</strong><strong>：</strong>中心支行党委各部、室部长（主任），各科室科长（主任）；党委组织部副部长、人事科副科长，内审科副科长，会计财务科副科长；辖区各支行党组书记、行长。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>2.</strong><strong>报备材料</strong><strong>：</strong>干部任免文件，干部任免审批表一式一份，A4纸打印，上报天津分行党委组织部干部科。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>3.</strong><strong>报备时限：</strong>任免职文件下达10日内报备。</p>\r\n\r\n<p style=\"text-align:justify\"><img src=\"/ckfinder/userfiles/result/image(18).png\" style=\"height:787px; width:427px\" /></p>\r\n</p>',11,'workFlow','2018-07-31 14:49:31','干部提拔2-机关','干部提拔2-机关'),(93,'/ckfinder/userfiles/result/image(18).png','<p><p><img src=\"/ckfinder/userfiles/result/image(18).png\" style=\"height:489px; width:591px\" /></p>\r\n</p>',11,'workFlow','2018-07-31 14:50:49','机关干部外出培训流程','机关干部外出培训流程'),(94,'/ckfinder/userfiles/result/image(18).png','<p><p style=\"text-align:justify\">学历变更工作流程</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">一、<strong>提交资料：</strong>学历学位证书（原件、复印件2份），学籍登记表或毕业生登记表（原件），学习成绩登记表（原件），学士、硕士学位授予决定（原件），硕士学位论文答辩相关材料，本人身份证（原件，复印件2份），网络教育、成人教育及开放式等&ldquo;专升本&rdquo;学历，均需同时提供&ldquo;专科&rdquo;学历证书（原件、复印件2份）。提供学历学位认证报告（原件，复印件2份），《中国人民银行在职人员学历学位变更认证审批表》（单位(科室)审批盖章2份）。</p>\r\n\r\n<p style=\"text-align:justify\">二、<strong>审核上报：</strong>人事科对符合学历学位变更条件人员提交的材料进行审核。审核合格后，填写《申请学历变更认定汇总表》，并在《人民银行学历学位变更认证审批表》和《申请学历变更认定汇总表》中填写单位意见并加盖公章。起草学历学位变更报告，上报呼和浩特中心支行审批；</p>\r\n\r\n<p style=\"text-align:justify\">三、<strong>公示：</strong>收到呼和浩特中心支行人事处公示通知后，按人员所在单位进行公示（公示期为10个工作日）。公示期满后，将公示结果上报呼和浩特中心支行人事处。</p>\r\n\r\n<p style=\"text-align:justify\">四、<strong>归档：</strong>收到呼和浩特中心支行人事处下发的学历学位变更通知，人事科印发学历变更通知，同时将《人民银行学历学位变更认证审批表》与其学籍材料一并存入本人档案。</p>\r\n\r\n<p style=\"text-align:justify\"><img src=\"/ckfinder/userfiles/result/image(18).png\" style=\"height:339px; width:389px\" /></p>\r\n</p>',11,'workFlow','2018-07-31 14:51:59','干部学历变更','干部学历变更'),(95,'/ckfinder/userfiles/result/image(18).png','<p><p style=\"text-align:justify\"><strong>工资计划管理流程简介</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>上级行计划下达：</strong></p>\r\n\r\n<p style=\"text-align:justify\">每年12月，由上级行下达全辖工资计划。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>计划分解：</strong></p>\r\n\r\n<p style=\"text-align:justify\">以全辖各单位上年度工资总额计划为基数，充分考虑到各种增（减）资因素做出安排的，主要包括：</p>\r\n\r\n<p style=\"text-align:justify\">（一）执行国家有关政策增资，如：分支机构养老保险改革增资、基本工资标准上调增资、艰苦边远地区津贴标准调整增资等因素。</p>\r\n\r\n<p style=\"text-align:justify\">（二）正常因素增资，如：正常晋升薪级工资增资、增人增资（减人减资）及翘尾（掉尾）（含聘用制人员）、转正定级增资及翘尾、晋升职务增资及翘尾以及高定基础绩效工资等级增资。</p>\r\n\r\n<p style=\"text-align:justify\">（三）业绩工资总额调整</p>\r\n\r\n<p style=\"text-align:justify\">按照国家关于事业单位工资收入分配制度改革文件精神和《中国人民银行分支行业绩工资总额管理暂行办法》的规定，调整各单位的业绩工资总额。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>下达计划：</strong></p>\r\n\r\n<p style=\"text-align:justify\">将党年工资总额计划层层分解下达到全辖各单位，并在分解下达时加强与会计财务部门的沟通协调。</p>\r\n\r\n<p style=\"text-align:justify\"><img src=\"/ckfinder/userfiles/result/image(18).png\" style=\"height:602px; width:458px\" /></p>\r\n</p>',11,'workFlow','2018-07-31 14:53:48','工资计划管理流程简介','工资计划管理流程简介'),(96,'/ckfinder/userfiles/result/image(18).png','<p><p style=\"text-align:justify\"><strong>工资统计管理</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>统计累计执行数：</strong></p>\r\n\r\n<p style=\"text-align:justify\">各单位月工资发放后，统计当年累计的在册人员、聘用制员工和合同制用工人员工资总额。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>会计财务对账：</strong></p>\r\n\r\n<p style=\"text-align:justify\">与会计财务核对累计执行数，保证工资总额累计执行数与会计列支数一致。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>（支行）上报劳资月报：</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>&nbsp;&nbsp; </strong>按照劳资月报要求， 分项目汇总工资总额执行数，上报</p>\r\n\r\n<p style=\"text-align:justify\">劳资月报。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>全辖劳资统计：</strong></p>\r\n\r\n<p style=\"text-align:justify\">汇总全辖工资执行总额。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>劳资季报、半年报、年报：</strong></p>\r\n\r\n<p style=\"text-align:justify\">按照上级行要求与模板，上报劳资季报、半年报、年报。</p>\r\n\r\n<p style=\"text-align:justify\"><img src=\"/ckfinder/userfiles/result/image(18).png\" style=\"height:801px; width:550px\" /></p>\r\n</p>',11,'workFlow','2018-07-31 14:56:07','工资统计管理流程简介','工资统计管理流程简介'),(97,'/ckfinder/userfiles/result/image(18).png','<p><p style=\"text-align:justify\"><strong>人员工资管理流程简介</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>发现调整事项：</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>参考文件</strong></p>\r\n\r\n<p style=\"text-align:justify\">1、《中国人民银行关于分支机构贯彻落实&lt;事业单位佛你工作人员收入分配制度改革方案&gt;和&lt;完善艰苦边远地区津贴制度实施方案&gt;的意见》（银发[2006]406号，秘密）；</p>\r\n\r\n<p style=\"text-align:justify\">2、《关于印发&lt;内蒙古自治区人民银行系统行员工资收入改进完善实施办法&gt;的通知》(蒙银发[2011]112号)。</p>\r\n\r\n<p style=\"text-align:justify\">一、日常调整</p>\r\n\r\n<p style=\"text-align:justify\">1、新录用行员：确定试用期工资；</p>\r\n\r\n<p style=\"text-align:justify\">2、调入人员：根据工资介绍信和相关政策确定工资；</p>\r\n\r\n<p style=\"text-align:justify\">3、转正定级、职务晋升、职称聘任：根据转正、任职、聘任文件，重新确定岗位等级变动后的人员工资。</p>\r\n\r\n<p style=\"text-align:justify\">4、岗位调整：人员岗位调整后，依据相关政策规定，重新确定岗位津贴（包括国家统一规定的津贴补贴和地区津贴）。</p>\r\n\r\n<p style=\"text-align:justify\">5、基础绩效日常调整：根据相关文件，同一岗位任职满相应年限，下月起调整基础绩效工资。</p>\r\n\r\n<p style=\"text-align:justify\">二、年度调整</p>\r\n\r\n<p style=\"text-align:justify\">普调薪级工资：每年的1月起，年度考核为称职及以上等次的工作人员，增加一级薪级工资。</p>\r\n\r\n<p style=\"text-align:justify\">缴养老统筹：由养老统筹计算基数，每年1月起变动。</p>\r\n\r\n<p style=\"text-align:justify\">三、工资调标</p>\r\n\r\n<p style=\"text-align:justify\">根据相关政策规定，根据新标准变动全体人员的工资项目金额。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>调整工资：</strong></p>\r\n\r\n<p style=\"text-align:justify\">进入工资管理系统，调整个人工资，并打印工资变动审批表。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>审批：</strong></p>\r\n\r\n<p style=\"text-align:justify\">处级干部（分行管理干部）岗位工资、薪级工资、基础绩效变动，向呼和浩特中心支行人事处发文请示；</p>\r\n\r\n<p style=\"text-align:justify\">支行科级干部（中支管理干部）岗位工资、薪级工资、基础绩效变动，向人事科发文请示。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>归档：</strong></p>\r\n\r\n<p style=\"text-align:justify\">1、《工资审批表》归入个人档案。</p>\r\n\r\n<p style=\"text-align:justify\"><img src=\"/ckfinder/userfiles/result/image(18).png\" style=\"height:458px; width:389px\" /></p>\r\n</p>',11,'workFlow','2018-07-31 14:57:51','人员工资管理流程简介','人员工资管理流程简介'),(98,'/ckfinder/userfiles/result/image(18).png','<p><p><img src=\"/ckfinder/userfiles/result/image(18).png\" style=\"height:498px; width:610px\" /></p>\r\n</p>',11,'workFlow','2018-07-31 14:58:42','党员领导干部民主生活会流程','党员领导干部民主生活会流程'),(99,'/ckfinder/userfiles/result/image(18).png','<p><p><img src=\"/ckfinder/userfiles/result/image(18).png\" style=\"height:581px; width:570px\" /></p>\r\n</p>',11,'workFlow','2018-07-31 14:59:32','专题组织生活会流程（组织人事部门）','专题组织生活会流程（组织人事部门）'),(100,'/ckfinder/userfiles/result/image(18).png','<p><p style=\"text-align:justify\"><strong>人员调动管理流程简介</strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>调出</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>个人申请：</strong></p>\r\n\r\n<p style=\"text-align:justify\">个人提出调动申请（手写签名），与接收单位商调函一起交由人事科。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>研究决定：</strong></p>\r\n\r\n<p style=\"text-align:justify\">人事科提交中支党委研究决定是否同意调动。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>向上级行请示：</strong></p>\r\n\r\n<p style=\"text-align:justify\">个人填写《中国人民银行天津分行工作人员调动审批表》，人事科行文请示天津分行，请示附件包括：个人调动申请、接受单位商调函、调动审批表。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>办理调出：</strong></p>\r\n\r\n<p style=\"text-align:justify\">上级行批复后，人事科办理调动手续。材料包括：干部职工介绍信、工资关系介绍信、档案。同时，将人力资源系统、工资系统和养老统筹系统内的信息转移至接受单位。停薪。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>调入</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>申请调入：</strong></p>\r\n\r\n<p style=\"text-align:justify\">接收支行、部门向党委提出调入人员申请。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>研究决定：</strong></p>\r\n\r\n<p style=\"text-align:justify\">人事科将调出单位开具的商调函和调入人员的《中国人民银行天津分行工作人员调动审批表》提交党委研究决定，如同意，人事部门办理签章手续；否则原样寄回。</p>\r\n\r\n<p style=\"text-align:justify\"><strong>办理调入：</strong></p>\r\n\r\n<p style=\"text-align:justify\">人事科收到上级行审批后的《中国人民银行天津分行工作人员调动审批表》后，联系调入人员报到。接收调入人员的干部职工介绍信、工资关系介绍信、档案。同时，接收人力资源系统、工资系统和养老统筹系统内的转移信息。起薪。</p>\r\n\r\n<p style=\"text-align:justify\"><img src=\"/ckfinder/userfiles/result/image(18).png\" style=\"height:469px; width:541px\" /></p>\r\n\r\n<p style=\"text-align:justify\"><img src=\"/ckfinder/userfiles/result/image(19).png\" style=\"height:395px; width:582px\" /></p>\r\n</p>',11,'workFlow','2018-07-31 15:01:10','人员调动管理流程简介','人员调动管理流程简介');
